<template>  <UMain class="mt-10 px-2">    <UContainer class="relative min-h-screen">      <UPage>        <UButton          class="!px-1 !py-1"          color="neutral"          icon="i-lucide-chevron-left"          label="Back to News"          to="/"          variant="ghost"        />        <div class="flex flex-col gap-3 mt-8">          <h1 class="text-4xl text-center font-medium max-w-3xl mx-auto mt-4">            {{ post?.title ?? '' }}          </h1>          <p class="text-muted text-center max-w-2xl mx-auto">            {{ post?.description }}          </p>          <div class="flex items-center justify-center gap-2 mt-2">            <div v-if="post?.author && post.author.avatar " class="flex items-center justify-center gap-3 mb-6">              <ClientOnly>                <NuxtImg                  :src="post.author.avatar"                  :alt="`Profile picture of ${post.author.name}`"                  width="40"                  height="40"                  loading="lazy"                  format="webp"                  class="w-10 h-10 rounded-full object-cover"                  sizes="40px"              />              </ClientOnly>              <div>                <div class="text-sm text-gray-700">                  By                  <NuxtLink v-if="post.author.slug" :to="`/author/${post.author.slug}`">{{ post.author.name }}</NuxtLink>                  <span v-else>{{ post.author.name }}</span>                </div>                <div class="text-xs text-gray-500">{{ post.date ? new Date(post.date).toLocaleDateString() : '' }}</div>              </div>            </div>            <p v-else class="text-sm text-gray-500 mb-8">              {{ post?.date ? new Date(post.date).toLocaleDateString() : '' }}            </p>          </div>          <NuxtImg              :alt="post?.title"              :src="post?.image"              class="rounded-xl w-9/12 max-h-[500px] object-cover object-center mx-auto"          />        </div>        <UPageBody class="max-w-4xl mx-auto article-container bg-gray-50 dark:bg-gray-800 rounded-xl p-4">          <div class="article-body prose prose-lg max-w-none mx-auto prose-img:rounded-lg prose-img:shadow-md prose-img:my-8 prose-headings:font-semibold prose-headings:text-gray-800 dark:prose-headings:text-gray-100 prose-a:text-primary hover:prose-a:underline prose-p:leading-relaxed prose-p:text-gray-700 dark:prose-p:text-gray-300">            <article class="prose prose-lg mx-auto" v-html="contentHtml" />          </div>          <section              v-if="post?.related && post.related.length"              aria-label="Related posts"              class="mt-12"          >            <div class="mb-6">              <h2 class="font-semibold text-xl">                Related posts              </h2>            </div>            <ul aria-label="Related blog posts" class="space-y-4">              <li                  v-for="related in post.related"                  :key="related.id"                  class="flex items-start gap-4"              >                <NuxtImg                    v-if="related.image"                    :alt="related.title"                    :src="related.image"                    class="w-20 h-14 object-cover rounded"                />                <div class="flex flex-col justify-center">                  <ULink                      :to="`/blog/${related.slug}`"                      class="text-md font-medium hover:text-primary leading-snug text-gray-900 dark:text-gray-100"                  >                    {{ related.title }}                  </ULink>                  <p class="text-xs text-gray-500 mt-1 hover:text-primary transition-colors">                    {{ related.date ? new Date(related.date).toLocaleDateString() : '' }}                  </p>                </div>              </li>            </ul>          </section>        </UPageBody>      </UPage>    </UContainer>  </UMain></template><script lang="ts" setup>import { computed } from 'vue'import { useRoute } from '#app'import { usePost } from '~/composables/usePost'const route = useRoute()const slug = route.params.slug as stringconst { data: post, pending, error } = await usePost(slug)const contentHtml = computed(() => post.value?.contentHtml ?? '')useSeoMeta({  title: post.value?.title || 'Blog Post',  description: post.value?.description || '',  ogTitle: post.value?.title || '',  ogDescription: post.value?.description || '',  ogImage: post.value?.image || '',  twitterCard: 'summary_large_image',  twitterTitle: post.value?.title || '',  twitterDescription: post.value?.description || '',  twitterImage: post.value?.image || ''})</script><style lang="scss" scoped>.article-body {  max-width: 800px;  margin: 0 auto;  padding: 2rem 1rem;}.article-container {  border-radius: 1rem;  padding: 1rem;}.prose img,.prose .rich-figure img {  max-width: 85%;  border-radius: 1rem;  box-shadow: 0 8px 30px rgba(0,0,0,0.1);  margin: 2.5rem auto;  display: block;  transition: transform 0.3s ease, box-shadow 0.3s ease;}.prose img:hover,.prose .rich-figure img:hover {  transform: scale(1.015);  box-shadow: 0 10px 36px rgba(0,0,0,0.12);}.prose figcaption {  font-size: 0.875rem;  font-style: italic;  color: #6b7280;  margin-top: 0.75rem;}.prose p {  line-height: 1.85;  color: #374151;  margin-bottom: 1.25rem;}.prose h2,.prose h3 {  color: #111827;  font-weight: 600;  margin-top: 3rem;  margin-bottom: 1rem;}</style>