<template>  <section class="max-w-3xl mx-auto py-12 px-4">    <h1 class="text-4xl font-bold mb-4">      {{ post?.title }}    </h1>    <p class="text-sm text-gray-500 mb-8">      {{ new Date(post?.publishedAt || '').toLocaleDateString() }}    </p>    <img      v-if="post?.cover?.url"      :src="post.cover.url"      :alt="post.cover.title"      class="rounded-xl mb-8"    />    <div class="prose prose-lg" v-html="html" />  </section></template><script setup lang="ts">  import type { ContentfulPost } from '~/types/contentful'  import { useRoute } from '#app'  import { usePost } from '~/composables/usePost'  const route = useRoute()  const slug = route.params.slug as string  const { post, html } = await usePost(slug)</script><style lang="scss" scoped>  .prose {    img {      max-width: 100%;      border-radius: 0.75rem;    }  }</style>